package main

import (
	"encoding/base64"
	"log"
	"net/http"
)

const (
	invalidPayload = ""
)

func handle(w http.ResponseWriter, r *http.Request) {
	decoded, err := base64.StdEncoding.DecodeString(invalidPayload)
	if err != nil {
		log.Println(err)
	}

	n, err := w.Write(decoded)
	if err != nil {
		log.Println(err)
	}

	log.Println(n)
}

func main() {
	http.HandleFunc("/", handle)
	log.Fatal(http.ListenAndServe(":8080", nil))
}
